import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

const NovaConversa = () => {
  const history = useHistory();
  const [conteudo, setConteudo] = useState('');
  const [id_conversa, setIdConversa] = useState('');

  const handleConteudoChange = (event) => {
    setConteudo(event.target.value);
  };

  const handleIdConversaChange = (event) => {
    setIdConversa(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Salvar a mensagem no servidor (usando a sua API do Laravel)
      const response = await axios.post('http://localhost:8000/api/mensagens', {
        conteudo,
        id_conversa: id_conversa, // Incluir o ID da conversa no corpo da requisição
      });
      const mensagemId = response.data.mensagem.id;

      // Redirecionar para a página da mensagem
      history.push(`/mensagens/${mensagemId}`);
    } catch (error) {
      console.error('Erro ao salvar a mensagem:', error);
    }
  };

  return (
    <div>
      <h1>Digite o conteúdo da mensagem:</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="conteudo">Conteúdo</label>
        <input
          type="text"
          id="conteudo"
          value={conteudo}
          onChange={handleConteudoChange}
          required
        />



        <button type="submit">Enviar Mensagem</button>
      </form>
    </div>
  );
};

export default NovaConversa;
