import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';

const Conversas = () => {
  const [conversas, setConversas] = useState([]);
  const [filteredConversas, setFilteredConversas] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    axios
      .get('http://localhost:8000/api/conversas')
      .then(response => {
        setConversas(response.data.data);
        setFilteredConversas(response.data.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleSearch = event => {
    const searchTerm = event.target.value.toLowerCase();
    const filteredConversas = conversas.filter(conversa =>
      conversa.titulo.toLowerCase().includes(searchTerm)
    );
    setFilteredConversas(filteredConversas);
  };

  const handleOpenModal = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div>
      <h2>Conversas</h2>
      <input
        type="text"
        placeholder="Pesquisar por título"
        onChange={handleSearch}
      />
      <table>
        <thead>
          <tr>
            <th>Título</th>
          </tr>
        </thead>
        <tbody>
          {filteredConversas.map(conversa => (
            <tr key={conversa.id} onClick={handleOpenModal}>
              <td>{conversa.titulo}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <Modal isOpen={isModalOpen} onRequestClose={handleCloseModal}>
        {/* Conteúdo do modal */}
      </Modal>
    </div>
  );
};

export default Conversas;
