import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

const NovaConversa = () => {
  const history = useHistory();
  const [titulo, setTitulo] = useState('');

  const handleTituloChange = (event) => {
    setTitulo(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Salvar o título no servidor (usando a sua API do Laravel)
      const response = await axios.post('/api/conversas', { titulo });
      const conversaId = response.data.id;

      // Redirecionar para a página do chat com o título e ID da conversa
      history.push(`/chat/${conversaId}`);

      // Iniciar uma nova conversa com o título salvo
      handleNovaConversa(titulo);
    } catch (error) {
      console.error('Erro ao salvar o título:', error);
    }
  };

  const handleNovaConversa = (tituloSalvo) => {
    setTitulo(tituloSalvo);
    // Outras ações para iniciar uma nova conversa com o título salvo
  };

  return (
    <div>
      <h1>Digite o título do chat:</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="titulo">Título</label>
        <input
          type="text"
          id="titulo"
          value={titulo}
          onChange={handleTituloChange}
          required
        />
        <button type="submit">Salvar e Iniciar Nova Conversa</button>
      </form>
    </div>
  );
};

export default NovaConversa;
