import React, { useState, useEffect } from 'react';
import axios from 'axios';
import $ from 'jquery';
import 'datatables.net/js/jquery.dataTables.min.js';

const Conversas = () => {
  const [conversas, setConversas] = useState([]);

  useEffect(() => {
    fetchConversas();
  }, []);

  useEffect(() => {
    const initializeDataTables = async () => {
      $('#conversasTable').DataTable();
    };

    initializeDataTables();
  }, [conversas]);

  const fetchConversas = async () => {
    try {
      const response = await axios.get('/api/conversas');
      setConversas(response.data);
    } catch (error) {
      console.error('Erro ao buscar as conversas:', error);
    }
  };

  const handleTituloClick = (conversa) => {
    console.log('Clicou no título da conversa:', conversa);
  };

  return (
    <div>
      <table id="conversasTable">
        <thead>
          <tr>
            <th>Título</th>
          </tr>
        </thead>
        <tbody>
          {conversas.map((conversa) => (
            <tr key={conversa.id} onClick={() => handleTituloClick(conversa)}>
              <td>{conversa.titulo}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Conversas;
