import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Mensagens = () => {
  const [mensagens, setMensagens] = useState([]);
  const [novaMensagem, setNovaMensagem] = useState('');

  useEffect(() => {
    axios.get('http://localhost:8000/api/mensagens')
      .then(response => {
        setMensagens(response.data.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleEnviarMensagem = () => {
    axios.post('http://localhost:8000/api/mensagens', { conteudo: novaMensagem })
      .then(response => {
        setMensagens([...mensagens, response.data.data]);
        setNovaMensagem('');
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div>
      <h2>Mensagens</h2>
      <div className="mensagens-container">
        {mensagens.map(mensagem => (
          <div
            key={mensagem.id}
            className={`mensagem ${mensagem.enviada ? 'enviada' : 'recebida'}`}
          >
            {mensagem.conteudo}
          </div>
        ))}
      </div>
      <div className="envio-mensagem">
        <input
          type="text"
          value={novaMensagem}
          onChange={event => setNovaMensagem(event.target.value)}
        />
        <button onClick={handleEnviarMensagem}>Enviar</button>
      </div>
    </div>
  );
};

export default Mensagens;
