import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Modal from 'react-modal';

const Chat = () => {
  const { conversaId } = useParams();
  const [mensagens, setMensagens] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedConversa, setSelectedConversa] = useState(null);
  const [mensagensModal, setMensagensModal] = useState([]);

  useEffect(() => {
    // Buscar as mensagens da conversa pelo ID
    axios.get(`http://localhost:8000/api/conversas/${conversaId}/mensagens`)
      .then(response => {
        setMensagens(response.data.data);
      })
      .catch(error => {
        console.error('Erro ao buscar mensagens:', error);
      });
  }, [conversaId]);

  const openModal = (conversa) => {
    setSelectedConversa(conversa);
    setModalIsOpen(true);

    axios.get(`http://localhost:8000/api/conversas/${conversa.id}/mensagens`)
      .then(response => {
        setMensagensModal(response.data.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const closeModal = () => {
    setSelectedConversa(null);
    setModalIsOpen(false);
  };

  return (
    <div>
      <h2>Chat</h2>
      <table>
        <thead>
          <tr>
            <th>TÃ­tulo</th>
          </tr>
        </thead>
        <tbody>
          {mensagens.map(conversa => (
            <tr key={conversa.id}>
              <td>
                <button onClick={() => openModal(conversa)}>
                  {conversa.titulo}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal}>
        {selectedConversa && (
          <div>
            <h3>{selectedConversa.titulo}</h3>
            <ul>
              {mensagensModal.map(mensagem => (
                <li key={mensagem.id}>{mensagem.conteudo}</li>
              ))}
            </ul>
          </div>
        )}
        <button onClick={closeModal}>Fechar</button>
      </Modal>
    </div>
  );
};

export default Chat;
