import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import { useHistory } from 'react-router-dom';
import { Link } from 'react-router-dom';

const AtendentePage = () => {
    const [conversas, setConversas] = useState([]);
    const [conversaFinalizada, setConversaFinalizada] = useState(false);
    const [mensagens, setMensagens] = useState([]);
    const [novaMensagem, setNovaMensagem] = useState('');

    useEffect(() => {
      fetchConversas();

      // Lógica para buscar as conversas a cada 2 segundos
      const interval = setInterval(() => {
        fetchConversas();
      }, 2000);

      return () => {
        clearInterval(interval);
      };
    }, []);

    const fetchConversas = () => {
      axios
        .get('http://localhost:8000/api/conversas')
        .then(response => {
          const conversas = response.data.data;
          setConversas(conversas);

          // Verificar se a conversa atual foi finalizada
          const conversaAtualFinalizada = conversas.find(conversa => conversa.finalizada);
          if (conversaAtualFinalizada) {
            setConversaFinalizada(true);
            setMensagens([]);
          }
        })
        .catch(error => {
          console.error(error);
        });
    };

    const fetchMensagens = (conversaId) => {
      axios
        .get(`http://localhost:8000/api/conversas/${conversaId}/mensagens`)
        .then(response => {
          setMensagens(response.data.data);
        })
        .catch(error => {
          console.error(error);
        });
    };

    const handleEnviarMensagem = () => {
      if (novaMensagem.trim() !== '') {
        const mensagemEnviada = {
          conteudo: novaMensagem,
          id_conversa: conversas[0].id, // Considerando a primeira conversa da lista
        };

        axios
          .post('http://localhost:8000/api/mensagens', mensagemEnviada)
          .then(response => {
            setMensagens([...mensagens, response.data.mensagem]);
            setNovaMensagem('');
          })
          .catch(error => {
            console.error(error);
          });
      }
    };

    const handleFinalizarConversa = () => {
      setMensagens([]);
      setConversaFinalizada(true);
    };

    const handleKeyDown = event => {
      if (event.key === 'Enter') {
        event.preventDefault();
        handleEnviarMensagem();
      }
    };

    if (conversaFinalizada) {
      return (
        <div>
          <p>Conversa finalizada.</p>
          <Link to="/nova-conversa">Iniciar nova conversa</Link>
        </div>
      );
    }

  return (
    <div className="chat-container">
      <h2>Área do Atendente</h2>
      <input
        type="text"
        placeholder="Pesquisar por título"
        onChange={handleSearch}
      />
      <table>
        <thead>
          <tr>
            <th>Título</th>
          </tr>
        </thead>
        <tbody>
          {filteredConversas.map(conversa => (
            <tr key={conversa.id}>
              <td>
                <button onClick={() => openModal(conversa)}>
                  {conversa.titulo}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal}>
        {selectedConversa && (
          <div>
            <h3>{selectedConversa.titulo}</h3>
            <div className="mensagens-container">
            {mensagensModal.map(mensagem => (
                <div
                key={mensagem.id}
                className={`mensagem ${mensagem.enviada ? 'enviada' : 'recebida'} ${
                  mensagem.enviada ? 'text-left' : ''
                }`}
              >
                {mensagem.enviada ? 'Atendente: ' : 'Usuário: '}
                {mensagem.conteudo}
              </div>
                ))}
            </div>
            <div className="envio-mensagem">
              <input
                  type="text"
                  value={novaMensagem}
                  onChange={event => setNovaMensagem(event.target.value)}
                  onKeyDown={handleKeyDown}
                />
                <button onClick={handleEnviarMensagem}>Enviar</button>
            </div>
            <div>
              <button onClick={handleFinalizarAtendimento}>
                Finalizar Atendimento
              </button>
              <button onClick={closeModal}>Fechar</button>
            </div>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default AreaAtendente;
